from bisect import bisect_left as B
class Solution:
    def solve(self,A, k):
        ans=10**19
        A.sort()
        n=len(A)
        for i in range(n):
            for j in range(i+1,n-1):
                x=A[i]+A[j]-k
                if x>=0:
                    ans=min(ans,abs(x+A[j+1]))
                else:
                    ind=max(B(A[j+1:],abs(x)),1)+j
                    if ind==n:
                        ans=min(ans,abs(x+A[ind-1]))
                    elif ind==n-1:
                        ans=min(ans,abs(x+A[ind]))
                    else:
                        if ind-1==j:
                            ans=min(ans,abs(x+A[ind]),abs(x+A[ind+1]))
                        else:
                            ans=min(ans,abs(x+A[ind-1]),abs(x+A[ind]),abs(x+A[ind+1]))
                #print(A[i],A[j],ans)

        return ans
